import React from "react";
import myImage from "../img/myImage.png";

class About extends React.Component {
  constructor() {
    super();
    this.state = {
      skills: [
        {
          id: "ReactJS_skill",
          content: "ReactJS",
          porcentage: "80%",
          value: "80"
        },
        {
          id: "ReactNative_skill",
          content: "ReactNative",
          porcentage: "80%",
          value: "80"
        },
        {
          id: "Python_skill",
          content: "Python",
          porcentage: "80%",
          value: "80"
        },
        {
          id: "JavaScript_skill",
          content: "JavaScript",
          porcentage: "80%",
          value: "80"
        },
        { id: "HTML5_skill", content: "HTML5", porcentage: "80%", value: "80" },
        { id: "CSS3_skill", content: "CSS3", porcentage: "75%", value: "75" },
        {
          id: "Express",
          content: "Express",
          porcentage: "70%",
          value: "70"
        },
        {
          id: "Node_JS",
          content: "NodeJS",
          porcentage: "60%",
          value: "60"
        },
        {
          id: "Mongo",
          content: "Mongo",
          porcentage: "60%",
          value: "60"
        },
        {
          id: "Swift",
          content: "Swift",
          porcentage: "25%",
          value: "25"
        }
      ],
      about_me: [
        {
          id: "first-p-about",
          content:
            "I'm a rising sophomore at Yale double majoring in Computer Science/Economics and Statistics."
        },
        {
          id: "second-p-about",
          content:
            "Interests: Reading, running, watching Youtube, and watching films. Some of my favorite films are Dark Knight, Casa Blance, and Jurassic Park"
        },
        {
          id: "third-p-about",
          content:
            "Extracurriculars: Vice President of Yale Computer Society, Logistics Chair of Yhack, Treasurer of Dwight Hall Socially Responsible Investment Fund, Stars I"
        },
        {
          id: "fourth p-about",
          content:
            "Extracurriculars: Vice President of Yale Computer Society, Logistics Chair of Yhack, Treasurer of Dwight Hall Socially Responsible Investment Fund, Stars I"
        },
        {
          id: "fifth p-about",
          content:
            "Volunteering: Event Producer Intern, Key Club President, Read Write Discover Branch Coordinator"
        }
      ],
      work_experience: [
        {
          role:"Software Engineer Intern",
          company: "Avenue",
          date: "August-Present",
          id: "first-p-about",
          content:
            "Currently working at a startup and developing an event planning app in React Native and Firestore."
        },
        {
          role:"Full Stack Developer - PPE4NYC",
          company: "PPE4NYC",
          date: "August-Present",
          id: "second-p-about",
          content:
            "Developing the website of a nonprofit that has delivered 10,000 PPE to people in need. Currently working on a content management system in Javascript. Also developing in ReactJS/Gatsby."
        },
        {
          role:"Machine Learning Intern - Yale Intelligent Computing Lab",
          company: "",
          date: "May-August",
          id: "third-p-about",
          content:
            "Trained and evaluated performance of spiking and artificial neural networks. Coded in Pytorch on Google Colab. Research found that spiking neural networks, a novel neural network that ran on a spiking time dependent framework had more adversarial robustness and energy efficiency than the artificial neural network"
        },
        {
          role:"Open Climate Collabathon Coordinator",
          company: "Yale OpenLab",
          date: "February-Present",
          id: "fourth p-about",
          content:
            "Planned, marketed, and handled logistics of a global open source initiative to tackle climate change through blockchain solutions. "
        }
      ]
    };
  }

  render() {
    return (
      <section id="about" className="about-mf sect-pt4 route">
        <div className="container">
          <div className="row">
            <div className="col-sm-12">
              <div className="box-shadow-full">
                <div className="row">
                  <div className="col-md-6">
                    <div className="row">
                      <div
                        className="col-sm-6 col-md-5"
                        style={{ margin: "0 auto" }}
                      >
                        <div
                          className="about-img"
                          style={{ textAlign: "center" }}
                        >
                          <img
                            className="img-fluid rounded b-shadow-a"
                            alt=""
                          />
                        </div>
                      </div>
                    </div>
                    <div className="skill-mf">
                      {/* <p className="title-s">Skill</p> */}
                      {this.state.skills.map(skill => {
                        return (
                          <React.Fragment key={skill.id}>
                            <span>{skill.content}</span>{" "}
                            <span className="pull-right">
                              {skill.porcentage}
                            </span>
                            <div className="progress">
                              <div
                                className="progress-bar"
                                role="progressbar"
                                style={{ width: skill.porcentage }}
                                aria-valuenow={skill.value}
                                aria-valuemin="0"
                                aria-valuemax="100"
                              ></div>
                            </div>
                          </React.Fragment>
                        );
                      })}
                    </div>
                  </div>
                  <div className="col-md-6">
                    <div className="about-me pt-4 pt-md-0">
                      <div className="title-box-2">
                        <h5 className="title-left">About Me</h5>
                      </div>
                      {this.state.about_me.map(content => {
                        return (
                          <p className="lead" key={content.id}>
                            {content.content}
                          </p>
                        );
                      })}
                      <div className="title-box-2">
                        <h5 className="title-left">Work Experience</h5>
                      </div>
                      {this.state.work_experience.map(content => {
                        return (
                          <div>
                            <h5>{content.role}</h5>
                            <h6>{content.company}</h6>
                            <h6>{content.date}</h6>
                            <p className="lead" key={content.id}>
                              {content.content}
                            </p>
                          </div>
                        );
                      })}
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    );
  }
}

export default About;
